import SwiftUI
import MapKit
import Parse

struct User{
    var name: String
    var password: String
    var age: String
    var location: CLLocationCoordinate2D
    var imageString: String?
    
    var email: String
    
    var image: UIImage?{
        get{
            let data = Data(base64Encoded: imageString ?? defaultImage )
        return UIImage(data: data!)
        }
        set{
            if newValue == nil{
                imageString = UIImage(systemName: "camera.on.rectangle")!.base64(format: .PNG)
            }
            else {
                imageString = newValue!.base64(format: .PNG)
            }
        }
    }
    
    init(_ pfUser: PFUser) {
        self.name = pfUser.username!
        self.password = pfUser.password ?? ""
        self.email = pfUser.email!
        self.age = pfUser["age"] as! String
        self.location = CLLocationCoordinate2D(latitude: pfUser["latitude"] as! Double, longitude: pfUser["longitude"] as! Double)
        self.imageString = pfUser["imageString"] as! String?
    }
    
    init(name: String, password: String, email: String, age: String, location: CLLocationCoordinate2D, imageString: String? = defaultImage) {
        self.name = name
        self.password = password
        self.email = email
        self.age = age
        self.location = location
        self.imageString = imageString
    }
    
}

public enum ImageFormat {
    case PNG
    case JPEG(CGFloat)
}

extension UIImage {
    
    public func base64(format: ImageFormat) -> String {
        var imageData: Data
        switch format {
        case .PNG: imageData = self.pngData()!
        case .JPEG(let compression): imageData = self.jpegData(compressionQuality: compression)!
        }
        return imageData.base64EncodedString()
    }
}

var defaultImage = ""
